mapper.select:
SELECT xt_login_logs.`xt_login_log_id`, xt_login_logs.`xt_userinfo_id`, xt_login_logs.`xt_login_logIP`, xt_login_logs.`xt_login_logtime`, xt_login_logs.`xt_login_logContent`, xt_login_logs.`xt_login_log_browser_type`, xt_login_logs.`xt_login_log_browser_name`, xt_login_logs.`xt_login_log_browser_version`, xt_login_logs.`xt_login_log_system`, xt_userinfo.`xt_userinfo_realName` FROM xt_login_logs xt_login_logs LEFT JOIN xt_userinfo xt_userinfo ON xt_login_logs.xt_userinfo_id=xt_userinfo.xt_userinfo_id WHERE 1=1
AND xt_login_logs.`xt_userinfo_id` = #{xt_userinfo_id}
ORDER BY xt_login_logs.`xt_login_logtime` DESC
