mapper.select:
SELECT xt_script.`xt_script_id`, xt_script.`xt_script_key`, xt_script.`xt_script_text`, xt_script.`xt_script_type`, xt_script.`xt_script_status`, xt_script.`xt_script_ctime`, xt_script.`xt_script_mtime`, xt_script.`xt_userinfo_id`, xt_script.`xt_script_title`, xt_script.`xt_script_content`, xt_script.`xt_script_search_filed`, xt_userinfo.`xt_userinfo_realName`, xt_script.`xt_script_valuefield`, xt_script.`xt_script_displayfield` FROM xt_script xt_script LEFT JOIN xt_userinfo AS xt_userinfo ON xt_script.xt_userinfo_id = xt_userinfo.xt_userinfo_id WHERE 1=1
AND xt_script_status = #{xt_script_status}
AND xt_script_type = #{xt_script_type}
AND xt_script_key = #{xt_script_key}
ORDER BY xt_script.`xt_script_ctime` DESC
