mapper.select:
SELECT b_seller_contacts_id, b_seller_id, b_seller_contacts_uname, b_seller_contacts_tel, b_seller_contacts_sex, b_seller_contacts_type, b_seller_contacts_turnover FROM b_seller_contacts WHERE 1=1
AND instr(b_seller_contacts_uname,#{b_seller_contacts_uname})
AND b_seller_contacts_tel = #{b_seller_contacts_tel}
AND b_seller_id = #{b_seller_id}
