mapper.select:
SELECT solr_schema_templet.solr_schema_templet_id, solr_schema_templet.solr_schema_templet_content, solr_schema_templet.solr_schema_templet_ctime, solr_schema_templet.solr_schema_templet_mtime, solr_schema_templet.xt_userinfo_id, solr_schema_templet.solr_schema_templet_status, solr_schema_templet.solr_schema_templet_title, xt_userinfo.xt_userinfo_realName FROM solr_schema_templet solr_schema_templet LEFT JOIN xt_userinfo xt_userinfo ON xt_userinfo.xt_userinfo_id = solr_schema_templet.xt_userinfo_id WHERE 1=1
AND solr_schema_templet.solr_schema_templet_status = #{solr_schema_templet_status}
AND solr_schema_templet.solr_schema_templet_title like '%${solr_schema_templet_title}%'
