mapper.select:
SELECT b_warehouse_location.`b_warehouse_location_id`, b_warehouse_location.`b_warehouse_location_name`, b_warehouse_location.`b_warehouse_location_space`, b_warehouse_location.`b_warehouse_location_width`, b_warehouse_location.`b_warehouse_location_height`, b_warehouse_location.`b_warehouse_location_length`, b_warehouse_location.`b_warehouse_id`, b_warehouse.`b_warehouse_name`, b_seller.`b_seller_name` FROM b_warehouse_location b_warehouse_location LEFT JOIN b_warehouse b_warehouse ON b_warehouse.b_warehouse_id=b_warehouse_location.b_warehouse_id LEFT JOIN b_seller b_seller ON b_seller.b_seller_id = b_warehouse.b_seller_id WHERE 1=1
AND instr(b_seller.b_seller_name,#{b_seller_name})
AND b_seller.b_seller_name = #{b_warehouse_type}
