mapper.select:
SELECT xt_change_pwd_id, user_name, login_id, sex, phone, mail, address, xt_attachment_id, ctime, status FROM xt_change_pwd WHERE 1=1
AND user_name LIKE CONCAT(CONCAT('%',#{user_name}),'%')
AND login_id = #{login_id}
AND sex = #{sex}
AND phone LIKE CONCAT(CONCAT('%',#{phone}),'%')
AND ctime LIKE CONCAT(CONCAT('%',#{ctime}),'%')
AND address LIKE CONCAT(CONCAT('%',#{address}),'%')
AND mail LIKE CONCAT(CONCAT('%',#{mail}),'%')
AND status = #{status}
