SELECT xt_functioninfo.xt_functioninfo_id, xt_functioninfo.xt_functioninfoName, xt_functioninfo.xt_functioninfoTitle, xt_functioninfo.xt_functioninfoURL, xt_functioninfo.xt_menuinfo_id, xt_functioninfo.xt_functioninfoMethod, xt_functioninfo.xt_functioninfoType, xt_functioninfo.xt_functioninfoIsAuthority, xt_functioninfo.xt_functioninfoStatus, xt_menuinfo.xt_menuinfo_title FROM xt_functioninfo xt_functioninfo LEFT JOIN xt_menuinfo xt_menuinfo ON xt_functioninfo.xt_menuinfo_id = xt_menuinfo.xt_menuinfo_id WHERE 1=1
AND xt_functioninfo.xt_functioninfo_id IN
#{item}

SELECT xt_functioninfo.xt_menuinfo_id, xt_menuinfo.xt_menuinfo_title FROM xt_functioninfo xt_functioninfo LEFT JOIN xt_menuinfo xt_menuinfo ON xt_functioninfo.xt_menuinfo_id = xt_menuinfo.xt_menuinfo_id WHERE 1=1 AND xt_functioninfo.xt_functioninfoStatus='0'
AND xt_functioninfo.xt_menuinfo_id = #{xt_menuinfo_id}
AND xt_functioninfo.xt_functioninfoType = #{xt_functioninfoType}
AND xt_menuinfo.xt_menuinfo_title like '%${xt_menuinfo_title}%'
AND xt_functioninfo.xt_functioninfoName like '%${xt_functioninfoName}%'
AND xt_functioninfo.xt_functioninfoIsAuthority = #{xt_functioninfoIsAuthority}
GROUP BY xt_functioninfo.xt_menuinfo_id,xt_menuinfo.xt_menuinfo_title

SELECT xt_functioninfo_id, xt_functioninfoName, xt_menuinfo_id FROM xt_functioninfo WHERE 1=1
AND xt_menuinfo_id = #{xt_menuinfo_id}
AND xt_functioninfoIsAuthority = #{xt_functioninfoIsAuthority}
