mapper.select:
SELECT xt_menuinfo_id, xt_menuinfo_parentId, xt_menuinfo_title, xt_menuinfo_url, xt_menuinfo_iconCls, xt_menuinfo_leaf, xt_menuinfo_images, xt_menuinfo_sort, xt_menuinfo_sys, xt_menuinfo_status FROM xt_menuinfo WHERE xt_menuinfo_parentId = #{xt_menuinfo_parentId}
AND xt_menuinfo_sys = #{xt_menuinfo_sys}
ORDER BY xt_menuinfo_sort ASC,xt_menuinfo_sys ASC
