mapper.select:
SELECT xt_message_id, from_id, to_id, xt_meesage_content, isread, ctime, readtime, xt_userinfo.xt_userinfo_realName as fromName, xtuserinfo.xt_userinfo_realName as toName FROM xt_message xt_message LEFT JOIN xt_userinfo xt_userinfo ON xt_userinfo.xt_userinfo_id = xt_message.from_id LEFT JOIN xt_userinfo xtuserinfo ON xtuserinfo.xt_userinfo_id = xt_message.to_id WHERE 1=1
AND isread = #{isread}
AND from_id = #{from_id}
AND to_id = #{to_id}
ORDER BY ctime ASC

SELECT
	from_id,
	COUNT(0) AS count
FROM 
	xt_message AS xt_message 
WHERE 1=1

	AND isread = #{isread}


	AND from_id = #{from_id}


	AND to_id = #{to_id}

GROUP BY from_id

mapper.update:
UPDATE
	xt_message
SET
	isread = #{isread},
	readtime = #{readtime}
WHERE 1=1
AND isread = 0
AND from_id = #{from_id}
AND to_id = #{to_id}
