mapper.select:
SELECT xt_notice.xt_notice_id, xt_notice.xt_title, xt_notice.xt_content, xt_notice.xt_userinfo_id, xt_notice.xt_isDel, xt_notice.xt_state, xt_notice.xt_createTime, xt_notice.xt_attachment_id, xt_notice.xt_attachment_id_, xt_userinfo.xt_userinfo_realName FROM xt_notice xt_notice LEFT JOIN xt_userinfo xt_userinfo ON xt_notice.xt_userinfo_id = xt_userinfo.xt_userinfo_id WHERE 1=1
AND xt_notice.xt_state = #{xt_state}
AND instr(xt_notice.xt_title,#{xt_title})
AND xt_notice.xt_userinfo_id IN
#{item}
