SELECT xt_post.xt_post_id, xt_post.xt_departinfo_id, xt_post.xt_post_parentId, xt_post.xt_post_name, xt_post.xt_post_desc, xt_post.xt_post_maxNum, xt_post.xt_post_isLeaf, xt_post.xt_post_image, xt_post.xt_post_grade, xt_departinfo.xt_departinfo_name FROM xt_post left join xt_departinfo on xt_post.xt_departinfo_id = xt_departinfo.xt_departinfo_id WHERE xt_post.xt_post_isdelete = 0
AND xt_post.xt_departinfo_id = #{xt_departinfo_id}
AND xt_post.xt_post_name LIKE CONCAT(CONCAT('%',#{xt_post_name}),'%')

